import React from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Plus, Download, Trash2, Eye, Calendar } from 'lucide-react';

const Dashboard = () => {
  const queryClient = useQueryClient();

  const { data: qrCodes, isLoading } = useQuery(
    'myQrCodes',
    () => axios.get('/api/qr/my-codes').then(res => res.data),
    {
      onError: (error) => {
        toast.error('Failed to load QR codes');
      }
    }
  );

  const deleteMutation = useMutation(
    (id) => axios.delete(`/api/qr/${id}`),
    {
      onSuccess: () => {
        queryClient.invalidateQueries('myQrCodes');
        toast.success('QR code deleted successfully');
      },
      onError: () => {
        toast.error('Failed to delete QR code');
      }
    }
  );

  const downloadMutation = useMutation(
    (id) => axios.post(`/api/qr/${id}/download`)
  );

  const handleDownload = (qrCode) => {
    const link = document.createElement('a');
    link.download = `${qrCode.title}.png`;
    link.href = qrCode.qrCodeData;
    link.click();
    downloadMutation.mutate(qrCode._id);
    toast.success('QR code downloaded!');
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this QR code?')) {
      deleteMutation.mutate(id);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">My QR Codes</h1>
          <Link to="/generate" className="btn btn-primary">
            <Plus className="w-4 h-4 mr-2" />
            Generate New QR Code
          </Link>
        </div>

        {qrCodes?.qrCodes?.length === 0 ? (
          <div className="text-center py-12">
            <div className="mb-4">
              <Eye className="w-16 h-16 mx-auto text-gray-400" />
            </div>
            <h3 className="text-xl font-semibold text-gray-600 mb-2">No QR codes yet</h3>
            <p className="text-gray-500 mb-6">Create your first QR code to get started</p>
            <Link to="/generate" className="btn btn-primary">
              <Plus className="w-4 h-4 mr-2" />
              Generate Your First QR Code
            </Link>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {qrCodes?.qrCodes?.map((qrCode) => (
              <div key={qrCode._id} className="card">
                <div className="card-header">
                  <h3 className="card-title">{qrCode.title}</h3>
                  <span className="text-sm text-gray-500 uppercase">{qrCode.type}</span>
                </div>
                
                <div className="mb-4 text-center">
                  <img
                    src={qrCode.qrCodeData}
                    alt={qrCode.title}
                    className="mx-auto border border-gray-200 rounded"
                    style={{ width: '150px', height: '150px' }}
                  />
                </div>

                <div className="text-sm text-gray-600 mb-4">
                  <p className="truncate mb-1">
                    <strong>Content:</strong> {qrCode.content}
                  </p>
                  <p className="flex items-center">
                    <Calendar className="w-4 h-4 mr-1" />
                    {new Date(qrCode.createdAt).toLocaleDateString()}
                  </p>
                  <p>Downloads: {qrCode.downloadCount}</p>
                </div>

                <div className="flex space-x-2">
                  <button
                    onClick={() => handleDownload(qrCode)}
                    className="flex-1 btn btn-primary text-sm py-2"
                  >
                    <Download className="w-4 h-4 mr-1" />
                    Download
                  </button>
                  <button
                    onClick={() => handleDelete(qrCode._id)}
                    disabled={deleteMutation.isLoading}
                    className="btn btn-secondary text-sm py-2"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {qrCodes?.totalPages > 1 && (
          <div className="mt-8 flex justify-center">
            <div className="flex space-x-2">
              {Array.from({ length: qrCodes.totalPages }, (_, i) => (
                <button
                  key={i + 1}
                  className={`px-3 py-2 rounded ${
                    qrCodes.currentPage === i + 1
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {i + 1}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
